import { Component, Input } from '@angular/core';
import { IMessage } from './message.type';
import { DomSanitizer } from '@angular/platform-browser';

@Component({
  selector: 'app-message',
  templateUrl: './message.component.html',
  styleUrls: ['./message.component.css']
})
export class MessageComponent {
  @Input()
  content!: string;
  @Input()
  content_type!:string;
  @Input()
  username!: string;
  @Input()
  pfp!: string;
  //constructor(private sanitizer: DomSanitizer) { 

  // }
}

<div class="message flex flex-row w-[150vh] break-words break-normal mt-[2vh] ">
    <div class="max-h-[6.5vh] max-w-[6.5vh]"><img class="rounded-full" src="data:image/jpeg;base64, {{pfp}}"></div>
    <div class="flex flex-col">
        <div class="textosexo ml-[2vh] "><p> {{username}} </p></div>
        <div class="textosexo ml-[2vh] text-[#ffffff] break-words "><p> {{content}} </p></div>
    </div>
</div>
